import { makeStyles } from '@material-ui/core/styles';

export default makeStyles(() => ({
    root:{
        // maxWidth: '100%'
        width: '200px',

    },
    img:{
        height: '250px',
    },
    media:{
        // height: 0,
        // paddingTop: "56.25%",
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        boxSizing: 'border-box',
    },
    cardActions:{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        backgroundColor: 'red',
        height: '100%',
        width:'100%'
    },
    cardContent:{
        display: 'flex',
        fontFamily: 'calibri',
    },
    cardHeader:{
        fontSize: '1rem',
        color: '#222222',
        fontWeight: 700,
        letterSpacing: '0.5px',
        padding: '5px 5px 0 0'
    },
    cardPrice:{
        fontSize: '1rem',
        letterSpacing: '0.5px',
        padding: '5px 0 0 5px',
    },
    cardDesc:{
        fontSize: '0.7rem'
    },
    grow:{
        flex: 1
    }
}));
