import { makeStyles } from '@material-ui/core/styles';

export default makeStyles(() => ({
    root:{
        width: '200px',

    },
    img:{
        height: '250px',
    },
    media:{
        width: '100%',
        height: '100%',
        objectFit: 'cover',
        boxSizing: 'border-box',
    },
    cardCartOverlayVisible:{
        width: '100%',
        height: '100%',
        backgroundColor: '#ffc19e67',
        justifyContent: 'center',
        alignItems: 'center',
    },
    cardCartOverlayHidden:{
        visibility: 'hidden',
    },
    cardContainer:{
        display: 'flex',
        fontFamily: 'calibri',
        width: '100%'
    },
    cardContent:{
        height: '70px',
        padding: '5px',
        display: 'flex',
        alignItems: 'center',
    },
    cardHeader:{
        fontSize: '0.9rem',
        color: '#222222',
        fontWeight: 700,
        letterSpacing: '0.5px',
        paddingRight: '5px'
    },
    cardPrice:{
        fontSize: '0.9rem',
        letterSpacing: '0.5px',
        paddingLeft: '5px',
    },
    cardDesc:{
        fontSize: '0.7rem'
    },
    grow:{
        flex: 1
    }
}));
