import React from 'react'
import { Card, CardMedia, CardContent, CardActions, Typography, IconButton } from '@material-ui/core';
import { AddShoppingCart } from '@material-ui/icons';
import useStyles from './styles'
import styles from './styles';

const Product = ({ product }) => {
    const classes = useStyles();

    return (
        <Card className={classes.root}>
            <div className={classes.img}>
            <CardMedia className={classes.media} image={product.image} title={product.name} />
            </div>
            <CardContent>
                <div className={classes.cardContent}>
                    <Typography variant='h6' className={classes.cardHeader} > 
                        {product.name}
                    </Typography>
                    <div className={classes.grow}/>
                    <Typography variant='h6' className={classes.cardPrice} > 
                        {product.price}
                    </Typography>
                </div>
                <Typography variant='body2' className={classes.cardDesc} color='textSecondary'>{product.description}</Typography>
            </CardContent>
            {/* <CardActions disableSpacing className={classes.cardAction} style={{backgroundColor:'red'}} > */}
                <IconButton aria-label='Add to Cart' >
                    <AddShoppingCart/>
                </IconButton>
            {/* </CardActions> */}
        </Card>
    )
}

export default Product
